// Helper functions
export function getChineseNameByDexId(dexId: number): string | null {
  const entry = pokemonNamesMapping[dexId.toString()];
  return entry?.['zh-cn'] || null;
}

export function getJapaneseNameByDexId(dexId: number): string | null {
  const entry = pokemonNamesMapping[dexId.toString()];
  return entry?.ja || null;
}

export function getEnglishNameByDexId(dexId: number): string | null {
  const entry = pokemonNamesMapping[dexId.toString()];
  return entry?.en || null;
}

export function getAllNamesByDexId(dexId: number): PokemonNames | null {
  const entry = pokemonNamesMapping[dexId.toString()];
  return entry || null;
}

// Find Pokémon by name (case-insensitive)
export function findPokemonByName(name: string, language: 'ja' | 'en' | 'zh-cn' = 'en'): number[] {
  const lowerName = name.toLowerCase();
  const results: number[] = [];
  
  for (const [dexId, names] of Object.entries(pokemonNamesMapping)) {
    if (names[language]?.toLowerCase().includes(lowerName)) {
      results.push(parseInt(dexId));
    }
  }
  
  return results;
}

// Get all available Pokémon IDs
export function getAllPokemonIds(): number[] {
  return Object.keys(pokemonNamesMapping).map(id => parseInt(id)).sort((a, b) => a - b);
}

// Check if a Pokémon ID exists in the mapping
export function hasPokemon(dexId: number): boolean {
  return dexId.toString() in pokemonNamesMapping;
}

// Get total count of Pokémon in the mapping
export function getPokemonCount(): number {
  return Object.keys(pokemonNamesMapping).length;
}
